<script>
document.addEventListener("DOMContentLoaded", () => {
    const toggleBtn = document.getElementById('toggleSidebar');
    const sidebarContainer = document.getElementById('sidebarContainer');
    const karierToggle = document.getElementById("informasi-karier");

    toggleBtn.addEventListener('click', () => {
        sidebarContainer.classList.toggle('hidden');
    });

    karierToggle.addEventListener('click', () => {
        const submenu = document.getElementById("karier-submenu");
        const arrow = document.getElementById("arrow-karier");
        submenu.classList.toggle("hidden");
        arrow.classList.toggle("rotate-45");
        arrow.classList.toggle("rotate-[-45deg]");
    });
});
</script>

